    } catch (error) {
      return res.status(error?.status || 500).send({
        message: error?.message || 'An unknown error has occurred',
      });
    }
  },
  async deleteUserByID(req, res) {
    try {
      await UserService.deleteUser(req.params.userID);
      return res.status(204).send();
    } catch (error) {
      return res.status(error?.status || 500).send({
        message: error?.message || 'An unknown error has occurred',
      });
    }
  },
